import React, { useState } from "react";
import {
    BrowserRouter as Router,
    Routes,
    Route,
    Link,
  } from "react-router-dom";

  import {
    AiOutlineHome,
    AiOutlineAlignLeft,
    AiOutlineCustomerService,
    AiOutlineUser,
    AiOutlineClose,
    AiOutlineMenu
  } from "react-icons/ai";

const Sidenav = () => {
    const [showMenu, setShowMenu] = useState(false);

    const toggleMenu = () => {
        setShowMenu(!showMenu);
    };

    const closeMenuOnMobile = () => {
        if (window.innerWidth <= 1150) {
        setShowMenu(false);
        }
    };

    return <nav className="sidenav">
        {/* <div id="mySidenav" className={`sidenav ${showMenu} ? "show-menu" : ""}`> */}
        <div>    
            <ul>
                <li>
                    <Link to={`/`}>
                        <span className="icon2"><AiOutlineHome /></span>
                        <span class="text">Home</span>
                    </Link>
                </li>
                <li>
                    <Link to={`/music`}>
                        <span className="icon2"><AiOutlineCustomerService /></span>
                        <span class="text">Music</span>
                    </Link>
                </li>
                <li>
                    <Link to={`/scribbles`}>
                        <span className="icon2"><AiOutlineAlignLeft /></span>
                        <span class="text">Scribbles</span>
                    </Link>
                </li>
                <li>
                    <Link to={`/about`}>
                        <span className="icon2"><AiOutlineUser /></span>
                        <span class="text">About</span>
                    </Link>
                </li>
            </ul>
            <div className="nav__close" id="nav-close">
                <span><AiOutlineClose /></span>
            </div>
        </div>
        <div className="nav__toggle" id="nav-toggle">
            <span><AiOutlineMenu /></span>
        </div>
    </nav>;
};

export default Sidenav;
